import React, { useState } from "react";

function App() {
  const [fileURL, setfileURL] = useState("");
  const [filename, setfilename] = useState("");

  // get file name and file url
  const handleFile = (e) => {
    setfilename(e.target.files[0].name);

    const reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setfileURL(reader.result);
    };
  };

  let uploadInput = null;
  const handleUploadMultipleFiles = (ev) => {
    ev.preventDefault();

    const data = new FormData();
    for (let i = 0; i < uploadInput.files.length; i++) {
      data.append("file", uploadInput.files[i]);
      fetch("http://localhost:5000/upload", {
        method: "POST",
        body: data,
      }).then((response) => {
        response.json().then((body) => {
          console.log(body);
          setfileURL(`http://localhost:5000/${body.filename}`);
          filename(body.filename);
          alert(
            `File ${body.filename} uploaded successfully to Downloads folder`
          );
        });
      });
    }
  };

  return (
    <>
      <form onSubmit={handleUploadMultipleFiles}>
        <div>
          <input
            ref={(ref) => {
              uploadInput = ref;
            }}
            type="file"
            multiple
          />
        </div>
        <br />
        <div>
          <button>Upload</button>
        </div>
      </form>
    </>
  );
}

export default App;
