import React, { useState } from "react";

function App() {
  const [fileURL, setfileURL] = useState("");
  const [filename, setfilename] = useState("");

  // const handleUploadFile = (ev) => {
  //   ev.preventDefault();

  //   const data = new FormData();
  //   data.append("file", uploadInput.files[0]);

  //   fetch("http://localhost:5000/upload", {
  //     method: "POST",
  //     body: data,
  //   }).then((response) => {
  //     response.json().then((body) => {
  //       console.log(body);
  //       setfileURL(`http://localhost:5000/${body.filename}`);
  //       alert(
  //         `File ${body.filename} uploaded successfully to Downloads folder`
  //       );
  //     });
  //   });
  // };

  let uploadInput;
  const handleUploadMultipleFiles = (ev) => {
    ev.preventDefault();

    const data = new FormData();
    for (let i = 0; i < uploadInput.files.length; i++) {
      data.append("file", uploadInput.files[i]);
    }

    fetch("http://localhost:5000/upload", {
      method: "POST",
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        console.log(body);
        setfileURL(`http://localhost:5000/${body.filename}`);
        // alert(
        //   `File ${body.filename} uploaded successfully to Downloads folder`
        // );
      });
    });
  };

  const uploadList = () => {
    var input = document.getElementById("file");
    var output = document.getElementById("fileList");
    var children = "";
    for (var i = 0; i < input.files.length; ++i) {
      children += "<li>" + input.files.item(i).name + "</li>";
    }
    output.innerHTML = "<ul>" + children + "</ul>";
  };

  return (
    <>
      <form onSubmit={handleUploadMultipleFiles}>
        <div>
          <input
            ref={(ref) => {
              uploadInput = ref;
            }}
            type="file"
            multiple
            onChange={uploadList}
          />
          <p>Selected files:</p>
          <div id="fileList"></div>
        </div>

        <br />
        <div>
          <button>Upload</button>
        </div>
      </form>
    </>
  );
}

export default App;
